import { createSlice, PayloadAction, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

export interface Login {
	name: string;
	email: string;
	password: string;
	confirmpassword: string;
}

const user = 'hello';

const initialState = {
	user: user ? user : null,
	isError: false,
	isSuccess: false,
	isLoading: false,
	message: '',
};

export const register = createAsyncThunk('auth/fetchLogin', async (user: Login, thunkAPI) => {
	try {
		const response = await axios.post('http://localhost:5000/api/register', user, { headers: { 'content-type': 'text/json' } });
		const data = await response;
		return data;
	} catch (err) {
		console.log(err);
	}
});

export const authSlice = createSlice({
	name: 'auth',
	initialState,
	// NOT ASYNC
	reducers: {
		reset: (state) => {
			state.isLoading = state.isError = state.isSuccess = false;
			state.message = '';
		},
	},
	// ASYNC
	extraReducers: (builder) => {
		builder.addCase(fetchLogin.pending, (state: any) => {
			console.log('fetching exercises...');
		}),
			builder.addCase(fetchLogin.fullfilled, (state: any, action: any) => {
				console.log('fetching exercises...');
			}),
			builder.addCase(fetchLogin.rejected, (state: any, action: any) => {
				console.log('fetching exercises...');
			});
	},
});

export const { reset } = authSlice.actions;
export default authSlice.reducer;
