import { createSlice, PayloadAction, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

export interface Login {
	name: string;
	email: string;
	password: string;
	confirmpassword: string;
}

const initialState = {
	name: '',
	email: '',
	isValid: false,
};

export const fetchLogin: any = createAsyncThunk('authenticate/fetchLogin', async (payload) => {
	try {
		const response = await axios.post('http://localhost:5000/api/register', payload, { headers: { 'content-type': 'text/json' } });
		const data = await response;
		return data;
	} catch (err) {
		console.log(err);
	}
});

export const authSlice = createSlice({
	name: 'authenticate',
	initialState,
	reducers: {},
	extraReducers: (builder) => {
		builder
			.addCase(fetchLogin.pending, (state: any, action: any) => {
				console.log('fetching exercises...');
			})
			.addCase(fetchLogin.fulfilled, (state: any, action: any) => {
				console.log('fetching exercises...');
			});
	},
});

export default authSlice.reducer;
