import { createSlice, PayloadAction } from '@reduxjs/toolkit';

const initialState = {
	isLoggedIn: false,
	username: null,
	userID: null,
};

export const authSlice = createSlice({
	name: 'auth',
	initialState,
	reducers: {
		loginUser: (state) => {
			state.isLoggedIn = true;
			state.userID = 1;
			state.username = 'bob';
		},
		logoutUser: (state) => {
			state = initialState;
		},
	},
});

export const { loginUser, logoutUser } = authSlice.actions;
export default authSlice.reducer;
